CREATE TABLE production_lines (
  production_line_id INT PRIMARY KEY,
  production_line_name VARCHAR(50) NOT NULL
);

CREATE TABLE waste_stats (
  id SERIAL PRIMARY KEY,
  production_line_id INT NOT NULL,
  total_waste_amount DECIMAL(10,2) NOT NULL,
  measured_timestamp TIMESTAMP
);

CREATE TABLE production_lines (
    production_line_id INT PRIMARY KEY,
    production_line_name STRING
);

CREATE TABLE waste_generation (
    waste_id INT PRIMARY KEY,
    production_line_id INT,
    waste_type STRING,
    waste_weight DECIMAL
);

CREATE TABLE production_rate (
    rate_id INT PRIMARY KEY,
    production_line_id INT,
    production_date DATE,
    production_rate DECIMAL
);

CREATE TABLE waste_disposal (
    disposal_id INT PRIMARY KEY,
    waste_id INT,
    disposal_date DATE,
    disposal_method STRING
);

CREATE TABLE production_line (
  id INT PRIMARY KEY,
  line_name VARCHAR(255) NOT NULL
);

CREATE TABLE waste_bin (
  id INT PRIMARY KEY,
  bin_location VARCHAR(255) NOT NULL,
  line_id INT NOT NULL,
  FOREIGN KEY (line_id) REFERENCES production_line(id)
);

CREATE TABLE waste_type (
  id INT PRIMARY KEY,
  type_name VARCHAR(255) NOT NULL
);
