tables.table_schema:
CREATE TABLE manufacturing_plants (
    plant_id INT NOT NULL PRIMARY KEY,
    plant_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    renewable_energy_usage DECIMAL NOT NULL
);

CREATE TABLE energy_sources (
    source_id INT NOT NULL PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL,
    source_capacity DECIMAL NOT NULL
);

CREATE TABLE Manufacturing_Plants (
    plant_id UUID PRIMARY KEY,
    plant_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    renewable_energy_source VARCHAR(100) NOT NULL
);

CREATE TABLE Renewable_Energy_Sources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(100) NOT NULL,
    efficiency DECIMAL
);

CREATE TABLE Energy_Sustainability (
    plant_id UUID,
    year INT,
    sustainability_rating INT,
    FOREIGN KEY (plant_id) REFERENCES Manufacturing_Plants(plant_id)
);
