tables.table_schema:
CREATE TABLE experiments (
  id INT PRIMARY KEY,
  experiment_name VARCHAR(255),
  experiment_date DATE,
  experiment_description TEXT
);

CREATE TABLE experiment_results (
   result_id UUID PRIMARY KEY,
   experiment_id UUID REFERENCES observation_data(experiment_id),
   result_value FLOAT,
   result_description TEXT
);

CREATE TABLE researchers (
   researcher_id UUID PRIMARY KEY,
   researcher_name VARCHAR(255),
   researcher_email VARCHAR(255),
   researcher_affiliation VARCHAR(255)
);

CREATE TABLE experiments (
    experiment_id INT PRIMARY KEY,
    experiment_name VARCHAR(255),
    experiment_date DATE,
    experiment_description TEXT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    researcher_email VARCHAR(255),
    researcher_department VARCHAR(255)
);

CREATE TABLE experiment (
  id INT PRIMARY KEY,
  study_id INT,
  experiment_name VARCHAR(255),
  date_conducted DATE
);

CREATE TABLE study (
  id INT PRIMARY KEY,
  study_name VARCHAR(255),
  researcher_name VARCHAR(255)
);

CREATE TABLE researcher (
  id INT PRIMARY KEY,
  researcher_name VARCHAR(255),
  department VARCHAR(255)
);

CREATE TABLE experiment_data (
  id INT PRIMARY KEY,
  experiment_id INT,
  data_type VARCHAR(50),
  data_value FLOAT
);

CREATE TABLE study_data (
  id INT PRIMARY KEY,
  study_id INT,
  data_type VARCHAR(50),
  data_value FLOAT
);
