tables.table_schema:
CREATE TABLE research_projects (
    project_id INT PRIMARY KEY,
    project_name STRING,
    start_date DATE,
    end_date DATE
);

CREATE TABLE project_milestones (
    milestone_id INT PRIMARY KEY,
    milestone_name STRING,
    deadline_date DATE,
    project_id INT REFERENCES research_projects(project_id)
);

CREATE TABLE Projects (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(255),
  department VARCHAR(100),
  project_id INT REFERENCES Projects(project_id)
);

CREATE TABLE Milestones (
  milestone_id INT PRIMARY KEY,
  milestone_name VARCHAR(255),
  milestone_date DATE,
  project_id INT REFERENCES Projects(project_id)
);

CREATE TABLE Deadlines (
  deadline_id INT PRIMARY KEY,
  deadline_date DATE,
  project_id INT REFERENCES Projects(project_id),
  milestone_id INT REFERENCES Milestones(milestone_id)
);

CREATE TABLE Tasks (
  task_id INT PRIMARY KEY,
  task_name VARCHAR(255),
  task_description TEXT,
  deadline_id INT REFERENCES Deadlines(deadline_id)
);
