tables.table_schema:
CREATE TABLE outliers_dataset (
    id SERIAL PRIMARY KEY,
    data_point FLOAT NOT NULL
);

CREATE TABLE outliers_data (
    record_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    value DECIMAL
);

CREATE TABLE anomaly_results (
    result_id UUID PRIMARY KEY,
    record_id UUID REFERENCES outliers_data(record_id),
    anomaly_score DECIMAL
);

CREATE TABLE anomalies (
    id INT PRIMARY KEY,
    record_id INT,
    record_value FLOAT,
    anomaly_score FLOAT
);

CREATE TABLE records (
    record_id INT PRIMARY KEY,
    data_timestamp TIMESTAMP,
    data_value FLOAT
);

CREATE TABLE outlier_results (
    result_id INT PRIMARY KEY,
    record_id INT,
    result_description VARCHAR(255)
);

CREATE TABLE outliers_dataset (
    record_id SERIAL PRIMARY KEY,
    data_value FLOAT NOT NULL,
    is_outlier BOOLEAN
);

CREATE TABLE anomaly_scores (
    score_id SERIAL PRIMARY KEY,
    record_id INT REFERENCES outliers_dataset(record_id),
    anomaly_score FLOAT
);

CREATE TABLE outlier_records (
    outlier_id SERIAL PRIMARY KEY,
    record_id INT REFERENCES outliers_dataset(record_id),
    outlier_reason VARCHAR
);

CREATE TABLE anomaly_logs (
    log_id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP,
    message TEXT
);
