tables.table_schema:
CREATE TABLE publications (
    publication_id INT PRIMARY KEY,
    title TEXT,
    authors TEXT[],
    publication_date DATE
);

CREATE TABLE keywords (
    keyword_id INT PRIMARY KEY,
    keyword TEXT
);

CREATE TABLE publication_keywords (
    publication_id INT,
    keyword_id INT,
    FOREIGN KEY (publication_id) REFERENCES publications(publication_id),
    FOREIGN KEY (keyword_id) REFERENCES keywords(keyword_id)
);

CREATE TABLE Publications(id INT PRIMARY KEY,
  title VARCHAR(255) NOT NULL,
  author VARCHAR(255) NOT NULL,
  publication_date DATE NOT NULL,
  abstract TEXT
);

CREATE TABLE Authors(id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  affiliation VARCHAR(255)
);

CREATE TABLE Keywords(id INT PRIMARY KEY,
  keyword VARCHAR(255) NOT NULL
);

CREATE TABLE PublicationAuthors(id INT PRIMARY KEY,
  publication_id INT NOT NULL,
  author_id INT NOT NULL,
  FOREIGN KEY (publication_id) REFERENCES Publications(id),
  FOREIGN KEY (author_id) REFERENCES Authors(id)
);

CREATE TABLE publications (
	publication_id UUID PRIMARY KEY,
	publication_title TEXT,
	publication_author TEXT,
	publication_date DATE
);

CREATE TABLE authors (
	author_id UUID PRIMARY KEY,
	author_name TEXT,
	author_affiliation TEXT
);

CREATE TABLE journals (
	journal_id UUID PRIMARY KEY,
	journal_name TEXT,
	journal_editor TEXT,
	journal_publisher TEXT
);

CREATE TABLE keywords (
	keyword_id UUID PRIMARY KEY,
	keyword_name TEXT
);
