tables.table_schema:
CREATE TABLE literature_review_projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50) NOT NULL,
    project_status VARCHAR(20) DEFAULT 'In Progress',
    project_deadline DATE
);

CREATE TABLE project_progress (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50) NOT NULL,
    project_status VARCHAR(20) NOT NULL
);

CREATE TABLE project(
  project_id INT PRIMARY KEY,
  project_name VARCHAR(50) NOT NULL,
  start_date DATE,
  end_date DATE
);

CREATE TABLE paper(
  paper_id INT PRIMARY KEY,
  paper_title VARCHAR(100) NOT NULL,
  authors VARCHAR(100),
  publication_date DATE
);

CREATE TABLE review(
  review_id INT PRIMARY KEY,
  reviewer_name VARCHAR(50) NOT NULL,
  review_date DATE,
  comments TEXT
);

CREATE TABLE project_paper(
  project_id INT REFERENCES project(project_id),
  paper_id INT REFERENCES paper(paper_id),
  PRIMARY KEY (project_id, paper_id)
);

CREATE TABLE project (
  project_id SERIAL PRIMARY KEY,
  project_name VARCHAR(255) NOT NULL,
  project_description TEXT
);

CREATE TABLE reviewer (
  reviewer_id SERIAL PRIMARY KEY,
  reviewer_name VARCHAR(255) NOT NULL,
  reviewer_email VARCHAR(255) UNIQUE
);

CREATE TABLE review (
  review_id SERIAL PRIMARY KEY,
  project_id INT REFERENCES project(project_id),
  reviewer_id INT REFERENCES reviewer(reviewer_id),
  submission_date TIMESTAMP
);

CREATE TABLE manuscript (
  manuscript_id SERIAL PRIMARY KEY,
  project_id INT REFERENCES project(project_id),
  manuscript_title VARCHAR(255) NOT NULL
);

CREATE TABLE feedback (
  feedback_id SERIAL PRIMARY KEY,
  review_id INT REFERENCES review(review_id),
  feedback_text TEXT,
  feedback_date TIMESTAMP
);
