tables.table_schema:
CREATE TABLE literature_review_data (
    id INT PRIMARY KEY,
    title TEXT,
    author TEXT,
    publication_date DATE
);

CREATE TABLE LiteratureReview (
	id INT PRIMARY KEY,
	author_name VARCHAR(100),
	publication_date DATE,
	review_text TEXT
);

CREATE TABLE Researcher (
	id INT PRIMARY KEY,
	name VARCHAR(100),
	institution VARCHAR(100),
	email VARCHAR(100)
);

CREATE TABLE AccessLog (
	id INT PRIMARY KEY,
	researcher_id INT,
	access_time TIMESTAMP,
	action VARCHAR(10)
);

CREATE TABLE UpdateLog (
	id INT PRIMARY KEY,
	researcher_id INT,
	update_time TIMESTAMP,
	updated_columns TEXT
);

CREATE TABLE collaborations (
    collaboration_id UUID PRIMARY KEY,
    researcher_id_1 UUID,
    researcher_id_2 UUID
);

CREATE TABLE access_logs (
    log_id UUID PRIMARY KEY,
    researcher_id UUID,
    access_date TIMESTAMP
);
