tables.table_schema:
CREATE TABLE literature_review (
    review_id INT PRIMARY KEY,
    review_title VARCHAR(255),
    review_date DATE,
    review_content TEXT
);

CREATE TABLE data_analysis (
    analysis_id INT PRIMARY KEY,
    analysis_type VARCHAR(50),
    analysis_date DATE,
    analysis_result TEXT
);

CREATE TABLE visualization (
    visualization_id INT PRIMARY KEY,
    visualization_type VARCHAR(50),
    visualization_date DATE,
    visualization_output TEXT
);

CREATE TABLE Paper_Categories (
  paper_id INT,
  category_id INT,
  category_name VARCHAR(50)
);

CREATE TABLE Data_Analysis (
  analysis_id INT,
  paper_id INT,
  analysis_date DATE,
  analysis_results TEXT
);

CREATE TABLE Visualization (
  visualization_id INT,
  analysis_id INT,
  visualization_date DATE,
  visualization_type VARCHAR(50)
);

CREATE TABLE authors (id INT PRIMARY KEY,
           author_name VARCHAR,
           affiliation VARCHAR);
