tables.table_schema:
CREATE TABLE LiteratureReview (
    id INT PRIMARY KEY,
    title TEXT,
    author TEXT,
    publication_date DATE,
    abstract TEXT
);

CREATE TABLE Reviewer (
    reviewer_id INT PRIMARY KEY,
    name TEXT,
    specialization TEXT
);

CREATE TABLE Literature (
    review_id INT PRIMARY KEY,
    review_title VARCHAR(255) NOT NULL,
    review_text TEXT,
    review_date DATE
);

CREATE TABLE Authors (
    author_id INT PRIMARY KEY,
    author_name VARCHAR(100) NOT NULL,
    author_email VARCHAR(255),
    author_affiliation VARCHAR(255)
);

CREATE TABLE Journals (
    journal_id INT PRIMARY KEY,
    journal_name VARCHAR(100) NOT NULL,
    journal_publisher VARCHAR(100),
    journal_issn VARCHAR(20)
);

CREATE TABLE Authors (
    author_id INT PRIMARY KEY,
    author_name TEXT,
    affiliation TEXT,
    contact_email TEXT
);

CREATE TABLE Keywords (
    keyword_id INT PRIMARY KEY,
    keyword_text TEXT
);

CREATE TABLE ReviewKeywords (
    review_id INT,
    keyword_id INT,
    PRIMARY KEY (review_id, keyword_id)
);
