tables.table_schema:
CREATE TABLE literature_reviews (
	id INT PRIMARY KEY,
	title VARCHAR(255),
	author VARCHAR(255),
	publication_date DATE
);

CREATE TABLE new_research_findings (
	id INT PRIMARY KEY,
	researcher VARCHAR(255),
	topic VARCHAR(255),
	published_date DATE
);

CREATE TABLE publications (
	id INT PRIMARY KEY,
	title VARCHAR(255),
	author VARCHAR(255),
	publication_date DATE
);

CREATE TABLE Updates (
  update_id INT PRIMARY KEY,
  publication_id INT REFERENCES Publications(publication_id),
  update_date DATE,
  update_description TEXT
);

CREATE TABLE literature_review (
    id INT PRIMARY KEY,
    title TEXT,
    author TEXT,
    publication_date DATE
);

CREATE TABLE research_findings (
    id INT PRIMARY KEY,
    findings TEXT,
    researcher TEXT,
    date_analyzed DATE
);

CREATE TABLE publications (
    id INT PRIMARY KEY,
    publication_title TEXT,
    authors TEXT,
    publication_date DATE
);

CREATE TABLE database_updates (
    id INT PRIMARY KEY,
    update_details TEXT,
    updated_by TEXT,
    update_date DATE
);

CREATE TABLE researchers (
    id INT PRIMARY KEY,
    researcher_name TEXT,
    research_interests TEXT,
    email TEXT
);
