CREATE TABLE citations (
    id INT PRIMARY KEY,
    source_id INT,
    citation_text TEXT,
    FOREIGN KEY (source_id) REFERENCES sources(id)
);

CREATE TABLE sources (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    author VARCHAR(255),
    publication_date DATE
);

CREATE TABLE keywords (
    id INT PRIMARY KEY,
    keyword VARCHAR(50)
);

CREATE TABLE citations (
