CREATE TABLE task_dependencies (
  dependency_id SERIAL PRIMARY KEY,
  task_id INT REFERENCES experimental_tasks(task_id),
  dependent_task_id INT REFERENCES experimental_tasks(task_id)
);

CREATE TABLE Experimenters (
    experimenter_id INT PRIMARY KEY,
    experimenter_name VARCHAR(255),
    experimenter_email VARCHAR(255)
);
