tables.table_schema:
CREATE TABLE experimental_procedures (
  id INT PRIMARY KEY,
  procedure_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE procedure_timeline (
  timeline_id INT PRIMARY KEY,
  procedure_id INT,
  timeline_date DATE,
  timeline_description TEXT
);

CREATE TABLE experimental_procedures (
  id UUID PRIMARY KEY,
  experiment_name VARCHAR(100),
  procedure_date DATE,
  researcher_name VARCHAR(100)
);

CREATE TABLE experiment_timeline (
    id SERIAL PRIMARY KEY,
    experiment_name VARCHAR(255) NOT NULL,
    experiment_date DATE NOT NULL,
    researcher_name VARCHAR(255) NOT NULL
);

CREATE TABLE experiment_notes (
    id SERIAL PRIMARY KEY,
    note TEXT NOT NULL,
    experiment_id INT NOT NULL,
    FOREIGN KEY (experiment_id) REFERENCES experiment_timeline(id)
);

CREATE TABLE experiments (
    experiment_id INT PRIMARY KEY,
    experiment_name VARCHAR(255) NOT NULL,
    date_started DATE,
    status ENUM('pending', 'ongoing', 'completed')
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    full_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE,
    department VARCHAR(100)
);

CREATE TABLE procedures (
    procedure_id INT PRIMARY KEY,
    procedure_name VARCHAR(255) NOT NULL,
    description TEXT,
    duration_minutes INT
);
