tables.table_schema:
CREATE TABLE genes (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    gene_sequence TEXT NOT NULL
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255) NOT NULL,
    patient_age INT NOT NULL,
    patient_gender VARCHAR(10)
);

CREATE TABLE genes (
  gene_id UUID PRIMARY KEY,
  gene_name VARCHAR(255),
  chromosome_number INT
);

CREATE TABLE variations (
  variation_id UUID PRIMARY KEY,
  gene_id UUID REFERENCES genes(gene_id),
  variation_type VARCHAR(50),
  impact_score FLOAT
);

CREATE TABLE patients (
  patient_id UUID PRIMARY KEY,
  patient_name VARCHAR(255),
  gender CHAR(1),
  age INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    age INT
);

CREATE TABLE patient_gene_data (
    patient_id UUID,
    gene_name VARCHAR(255),
    genotype VARCHAR(10),
    CONSTRAINT fk_patient_gene_data FOREIGN KEY (patient_id) REFERENCES gene_variant(variant_id)
);

CREATE TABLE research_project (
    project_id UUID PRIMARY KEY,
    project_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE researcher (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR(255),
    specialty VARCHAR(100),
    institution VARCHAR(255)
);

CREATE TABLE project_researcher (
    project_id UUID,
    researcher_id UUID,
    CONSTRAINT fk_project_researcher_project FOREIGN KEY (project_id) REFERENCES research_project(project_id),
    CONSTRAINT fk_project_researcher_researcher FOREIGN KEY (researcher_id) REFERENCES researcher(researcher_id)
);
