tables.table_schema:
CREATE TABLE peer_review_data (
  review_id INT,
  reviewer_username VARCHAR(50),
  completed_date DATE,
  completion_status VARCHAR(20)
);

CREATE TABLE PeerReviewCompletionRates (
    id SERIAL PRIMARY KEY,
    reviewer_id INT NOT NULL,
    paper_id INT NOT NULL,
    completion_rate FLOAT
);

CREATE TABLE ReviewerPerformance (
    id SERIAL PRIMARY KEY,
    reviewer_id INT NOT NULL,
    total_reviews INT,
    avg_rating FLOAT
);

CREATE TABLE CustomizableReports (
    id SERIAL PRIMARY KEY,
    report_id INT NOT NULL,
    report_name TEXT NOT NULL,
    report_description TEXT
);

CREATE TABLE peer_reviews (
  id INT PRIMARY KEY,
  reviewer_id INT,
  completion_rate FLOAT
);

CREATE TABLE reviewers (
  id INT PRIMARY KEY,
  name VARCHAR(255),
