tables.table_schema:
CREATE TABLE conflicts_of_interest (
    reviewer_id INT, 
    paper_id INT, 
    conflict_type VARCHAR
);

CREATE TABLE reviewers (
    reviewer_id INT,
    name VARCHAR,
    institution VARCHAR
);

CREATE TABLE conflicts (
    id SERIAL PRIMARY KEY,
    reviewer_id INT NOT NULL,
    paper_id INT NOT NULL,
    conflict_reason TEXT
);

CREATE TABLE reviewers (
    id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    email TEXT UNIQUE
);

CREATE TABLE papers (
    id SERIAL PRIMARY KEY,
    title TEXT NOT NULL,
    publication_date DATE,
    abstract TEXT
);

CREATE TABLE PeerReviewAssignments (
    id UUID PRIMARY KEY,
    reviewer_id UUID,
    paper_id UUID,
    conflict_of_interest BOOL
);

CREATE TABLE Researchers (
    id UUID PRIMARY KEY,
    name VARCHAR,
    institution VARCHAR
);

CREATE TABLE ConflictOfInterestReasons (
    id UUID PRIMARY KEY,
    reason TEXT
);

CREATE TABLE ReviewerScores (
    id UUID PRIMARY KEY,
    reviewer_id UUID,
    paper_id UUID,
    score INT
);
