tables.table_schema:
CREATE TABLE survey_data (
    survey_id INT PRIMARY KEY,
    survey_question VARCHAR(255),
    survey_response VARCHAR(255),
    respondent_id INT
);

CREATE TABLE SurveyData (
  survey_id INT PRIMARY KEY,
  survey_question TEXT,
  survey_answer TEXT
);

CREATE TABLE ExternalDataset (
  dataset_id INT PRIMARY KEY,
  dataset_name TEXT,
  external_data TEXT
);

CREATE TABLE SurveyData ( 
    survey_id INT PRIMARY KEY, 
    survey_title VARCHAR(255), 
    survey_date DATE 
);

CREATE TABLE ExternalDataset ( 
    dataset_id INT PRIMARY KEY, 
    dataset_name VARCHAR(255), 
    dataset_type VARCHAR(50) 
);

CREATE TABLE SurveyResponses ( 
    response_id INT PRIMARY KEY, 
    survey_id INT, 
    respondent_name VARCHAR(255), 
    response_text TEXT 
);

CREATE TABLE MergedData ( 
    merge_id INT PRIMARY KEY, 
    survey_id INT, 
    dataset_id INT, 
    response_id INT 
);
