tables.table_schema:
CREATE TABLE researcherCodingData (
  researcher_id INT,
  code_id INT,
  code_description TEXT,
  coding_frequency INT
);

CREATE TABLE researchers (
    researcher_id INT,
    researcher_name TEXT,
    affiliation TEXT
);

CREATE TABLE code_frequencies (
    code_id INT,
    researcher_id INT,
    code TEXT,
    frequency INT
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    research_interests TEXT
);

CREATE TABLE QualitativeData (
    data_id INT PRIMARY KEY,
    data_text TEXT NOT NULL,
    research_type VARCHAR(50)
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50)
);

CREATE TABLE code_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50),
  description TEXT
);

CREATE TABLE coding_data (
  data_id INT PRIMARY KEY,
  researcher_id INT,
  category_id INT,
  data_text TEXT
);

CREATE TABLE data_analysis (
  analysis_id INT PRIMARY KEY,
  data_id INT,
  code_frequency INT,
  analysis_result TEXT
);

CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50) NOT NULL
);

CREATE TABLE CodingProjects (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(50) NOT NULL,
  researcher_id INT NOT NULL,
  FOREIGN KEY (researcher_id) REFERENCES Researchers(researcher_id)
);

CREATE TABLE CodeFrequencies (
  code_id INT PRIMARY KEY,
  code_name VARCHAR(50) NOT NULL
);

CREATE TABLE ResearchResults (
  result_id INT PRIMARY KEY,
  data_id INT NOT NULL,
  result_description TEXT NOT NULL,
  FOREIGN KEY (data_id) REFERENCES ResearchData(data_id)
);
