CREATE TABLE multimedia_data ( 
    id INT PRIMARY KEY, 
    image_url VARCHAR(255), 
    video_url VARCHAR(255), 
    description TEXT 
);

CREATE TABLE multimedia_data (
    id INT PRIMARY KEY,
    image_url VARCHAR(255),
    video_url VARCHAR(255),
    description TEXT
);
CREATE TABLE analysis_results (
    id INT PRIMARY KEY,
    data_id INT,
    analysis_type VARCHAR(50),
    result TEXT,
    FOREIGN KEY (data_id) REFERENCES multimedia_data(id)
);

CREATE TABLE image_tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) NOT NULL
);

CREATE TABLE video_tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) NOT NULL
);

CREATE TABLE images (
  image_id INT PRIMARY KEY,
  image_url VARCHAR(255) NOT NULL,
  image_description TEXT,
  image_tags JSONB
);

CREATE TABLE videos (
  video_id INT PRIMARY KEY,
  video_url VARCHAR(255) NOT NULL,
  video_length INTERVAL,
  video_resolution VARCHAR(10)
);

CREATE TABLE image_metadata (
  metadata_id INT PRIMARY KEY,
  image_id INT REFERENCES images(image_id),
  metadata_key VARCHAR(50) NOT NULL,
  metadata_value VARCHAR(255)
);

CREATE TABLE video_metadata (
  metadata_id INT PRIMARY KEY,
  video_id INT REFERENCES videos(video_id),
  metadata_key VARCHAR(50) NOT NULL,
  metadata_value VARCHAR(255)
);

CREATE TABLE image_video_mapping (
  image_id INT REFERENCES images(image_id),
  video_id INT REFERENCES videos(video_id),
  PRIMARY KEY (image_id, video_id)
);
