tables.table_schema:
CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_topic VARCHAR(100),
    research_text TEXT
);

CREATE TABLE Researchers (
 researcher_id INT PRIMARY KEY,
 researcher_name STRING
);

CREATE TABLE TextualData (
 data_id INT PRIMARY KEY,
 researcher_id INT,
 data_content STRING
);

CREATE TABLE Researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name STRING,
    research_topic STRING,
    affiliation STRING
);

CREATE TABLE AnalysisResults (
    result_id UUID PRIMARY KEY,
    result_description TEXT,
    result_category STRING,
    data_id UUID REFERENCES TextualData(data_id)
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    affiliation VARCHAR(255)
);

CREATE TABLE researchers (
 researcher_id INT PRIMARY KEY,
 researcher_name VARCHAR(100),
 researcher_department VARCHAR(100),
 researcher_email VARCHAR(100)
);

CREATE TABLE coding_categories (
 category_id INT PRIMARY KEY,
 category_name VARCHAR(100),
 category_description TEXT
);

CREATE TABLE coding_entries (
 entry_id INT PRIMARY KEY,
 entry_content TEXT,
 category_id INT,
 CONSTRAINT fk_category FOREIGN KEY (category_id) REFERENCES coding_categories(category_id)
);
