tables.table_schema:
CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL
);

CREATE TABLE Researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR(100) NOT NULL,
    research_project VARCHAR(200) NOT NULL
);

CREATE TABLE Research_Data (
    data_id UUID PRIMARY KEY,
    research_topic VARCHAR(150) NOT NULL,
    data_text TEXT NOT NULL
);

CREATE TABLE Queries (
    query_id UUID PRIMARY KEY,
    researcher_id UUID REFERENCES Researchers(researcher_id),
    query_text TEXT NOT NULL
);

CREATE TABLE Results (
    result_id UUID PRIMARY KEY,
    query_id UUID REFERENCES Queries(query_id),
    result_data TEXT NOT NULL
);

CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(100),
  institution VARCHAR(100),
  expertise VARCHAR(100)
);
