tables.table_schema:
CREATE TABLE research_data (
    id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    research_topic VARCHAR(255),
    publication_date DATE
);

CREATE TABLE collaborators (
    id INT PRIMARY KEY,
    name TEXT,
    email TEXT,
    affiliation TEXT
);

CREATE TABLE stakeholders (
    id INT PRIMARY KEY,
    name TEXT,
    email TEXT,
    role TEXT
);

CREATE TABLE research_project (
    id INT PRIMARY KEY,
    project_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE research_collaborators (
    collaborator_id UUID PRIMARY KEY,
    project_id UUID REFERENCES research_projects(project_id),
    collaborator_name TEXT NOT NULL,
    organization TEXT
);

CREATE TABLE research_results (
    result_id UUID PRIMARY KEY,
    project_id UUID REFERENCES research_projects(project_id),
    result TEXT NOT NULL,
    result_date DATE NOT NULL
);
