CREATE TABLE Authors ( 
    author_id SERIAL PRIMARY KEY, 
    author_name VARCHAR(100) NOT NULL 
);

CREATE TABLE Books ( 
    book_id SERIAL PRIMARY KEY, 
    book_title VARCHAR(255) NOT NULL, 
    publication_date DATE 
);

CREATE TABLE Keywords ( 
    keyword_id SERIAL PRIMARY KEY, 
    keyword_name VARCHAR(50) NOT NULL 
);

CREATE TABLE Citations ( 
    citation_id SERIAL PRIMARY KEY, 
    book_id INT REFERENCES Books(book_id), 
    cited_book_id INT REFERENCES Books(book_id), 
    citation_date DATE 
);

CREATE TABLE authors (author_id INT PRIMARY KEY,
 author_name VARCHAR(100) NOT NULL,
 affiliation VARCHAR(100) NOT NULL
);

CREATE TABLE publications (publication_id INT PRIMARY KEY,
 title VARCHAR(200) NOT NULL,
 abstract TEXT,
 publication_date DATE
);

CREATE TABLE keywords (keyword_id INT PRIMARY KEY,
 keyword_name VARCHAR(50) NOT NULL
);

CREATE TABLE publication_keywords (publication_id INT,
 keyword_id INT,
 PRIMARY KEY(publication_id, keyword_id),
 FOREIGN KEY(publication_id) REFERENCES publications(publication_id),
 FOREIGN KEY(keyword_id) REFERENCES keywords(keyword_id)
);

CREATE TABLE citations (citation_id INT PRIMARY KEY,
 citing_publication_id INT,
 cited_publication_id INT,
 FOREIGN KEY(citing_publication_id) REFERENCES publications(publication_id),
 FOREIGN KEY(cited_publication_id) REFERENCES publications(publication_id)
);
