tables.table_schema:
CREATE TABLE research_data (
  id INT PRIMARY KEY,
  source VARCHAR(50),
  data_text TEXT,
  data_date DATE
);

CREATE TABLE StatisticalAnalysis (
  analysis_id UUID PRIMARY KEY,
  dataset_id UUID REFERENCES DatasetCleaning(dataset_id),
  analysis_type STRING,
  analysis_result JSONB
);

CREATE TABLE Studies (
    study_id UUID PRIMARY KEY,
    study_name STRING,
    research_field STRING,
    dataset_location STRING
);

CREATE TABLE Researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name STRING,
    institution STRING,
    email STRING
);

CREATE TABLE PreprocessedData (
    data_id UUID PRIMARY KEY,
    dataset_id UUID REFERENCES Datasets(dataset_id),
    preprocessing_steps STRING,
    cleaned_data_location STRING
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    institution VARCHAR(255) NOT NULL
);

CREATE TABLE Datasets (
    dataset_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    source VARCHAR(255) NOT NULL
);

CREATE TABLE CleaningSteps (
    step_id INT PRIMARY KEY,
    step_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE PreprocessingSteps (
    preprocess_id INT PRIMARY KEY,
    preprocess_name VARCHAR(255) NOT NULL,
    action VARCHAR(255) NOT NULL
);

CREATE TABLE StatisticalAnalysis (
    analysis_id INT PRIMARY KEY,
    analysis_name VARCHAR(255) NOT NULL,
    technique VARCHAR(255) NOT NULL
);
