tables.table_schema:
CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50)
);

CREATE TABLE dataset (
  data_id INT PRIMARY KEY,
  data_value FLOAT
);

CREATE TABLE Researchers (id INT PRIMARY KEY, name VARCHAR(255) NOT NULL, institution VARCHAR(255) NOT NULL);
CREATE TABLE Datasets (id INT PRIMARY KEY, name VARCHAR(255) NOT NULL, researcher_id INT NOT NULL,
    FOREIGN KEY (researcher_id) REFERENCES Researchers(id));
CREATE TABLE DataPoints (id INT PRIMARY KEY, value FLOAT NOT NULL, dataset_id INT NOT NULL,
    FOREIGN KEY (dataset_id) REFERENCES Datasets(id));
CREATE TABLE DescriptiveStatistics (id INT PRIMARY KEY, mean FLOAT NOT NULL, median FLOAT NOT NULL, range FLOAT NOT NULL, dataset_id INT NOT NULL,
    FOREIGN KEY (dataset_id) REFERENCES Datasets(id));

Create Table researcher_logs (
    log_id SERIAL PRIMARY KEY,
    researcher_id INT,
    log_description TEXT,
    log_time TIMESTAMP
);
