CREATE TABLE climate_measurements (
  id INT PRIMARY KEY,
  measurement_date DATE,
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE biodiversity_records (
  id INT PRIMARY KEY,
  species_name VARCHAR(50),
