tables.table_schema:
CREATE TABLE researchers (
  researcher_id SERIAL PRIMARY KEY,
  researcher_name VARCHAR(50) NOT NULL,
  researcher_email VARCHAR(100) UNIQUE
);

CREATE TABLE consent_forms (
    id INT PRIMARY KEY,
    researcher_id INT,
    form_text TEXT,
    date_signed DATE
);

CREATE TABLE researchers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    institution VARCHAR(255)
);
