tables.table_schema:
CREATE TABLE ethics_review_board ( 
   id INT PRIMARY KEY, 
   researcher_name VARCHAR(50), 
   department VARCHAR(50), 
   conflict_description TEXT 
 );

CREATE TABLE conflict_status ( 
   id INT PRIMARY KEY, 
   researcher_id INT, 
   status VARCHAR(20), 
   review_date DATE 
 );

CREATE TABLE conflicts_of_interest (
    id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    research_project VARCHAR(255),
    conflict_description TEXT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    email VARCHAR(255),
    institution VARCHAR(255)
);

CREATE TABLE review_board (
    board_id INT PRIMARY KEY,
    board_name VARCHAR(255),
    board_email VARCHAR(255)
);

CREATE TABLE researchers (
	researcher_id INT PRIMARY KEY,
	researcher_name VARCHAR(100) NOT NULL,
	researcher_email VARCHAR(255),
	research_area VARCHAR(100)
);
