tables.table_schema:
CREATE TABLE research_institutions (
    institution_id INT PRIMARY KEY,
    institution_name VARCHAR(100),
    institution_address VARCHAR(255),
    institution_country VARCHAR(50)
);

CREATE TABLE investigators (
    investigator_id INT PRIMARY KEY,
    investigator_name VARCHAR(100),
    investigator_email VARCHAR(255),
    investigator_phone VARCHAR(20)
);

CREATE TABLE ResearchInstitutions (
  institution_id INT PRIMARY KEY,
  institution_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);

CREATE TABLE Allegations (
  allegation_id INT PRIMARY KEY,
  allegation_description TEXT NOT NULL,
  researcher_id INT,
  FOREIGN KEY (researcher_id) REFERENCES Researchers (researcher_id)
);

CREATE TABLE Investigations (
  investigation_id INT PRIMARY KEY,
  investigation_description TEXT NOT NULL,
  allegation_id INT,
  FOREIGN KEY (allegation_id) REFERENCES Allegations (allegation_id)
);

CREATE TABLE ResearchInstitutions (
  institution_id INT PRIMARY KEY,
  institution_name VARCHAR(100) NOT NULL
);

CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(100) NOT NULL,
  institution_id INT NOT NULL,
  FOREIGN KEY (institution_id) REFERENCES ResearchInstitutions (institution_id)
);

CREATE TABLE Evidence (
  evidence_id INT PRIMARY KEY,
  investigation_id INT NOT NULL,
  evidence_description TEXT,
  FOREIGN KEY (investigation_id) REFERENCES Investigations (investigation_id)
);
