tables.table_schema:
CREATE TABLE researchers ( 
    researcher_id INT PRIMARY KEY, 
    name VARCHAR(50), 
    department VARCHAR(50), 
    completion_status BOOLEAN 
);

CREATE TABLE training_courses ( 
    course_id INT PRIMARY KEY, 
    course_name VARCHAR(50), 
    course_description TEXT, 
    completion_date DATE 
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    name TEXT NOT NULL,
    department TEXT,
    email TEXT
);

CREATE TABLE training_modules (
    module_id INT PRIMARY KEY,
    module_name TEXT NOT NULL,
    module_description TEXT
);

CREATE TABLE training_completion (
    completion_id INT PRIMARY KEY,
    researcher_id INT REFERENCES researchers(researcher_id),
    module_id INT REFERENCES training_modules(module_id),
    completion_date DATE
);

CREATE TABLE compliance_officers (
    officer_id INT PRIMARY KEY,
    name TEXT NOT NULL,
    department TEXT,
    email TEXT
);

CREATE TABLE ethics_compliance_officers (
	officer_id INT PRIMARY KEY,
	officer_name VARCHAR(50) NOT NULL,
	officer_email VARCHAR(50) UNIQUE
);

CREATE TABLE researchers (
	researcher_id INT PRIMARY KEY,
	researcher_name VARCHAR(50) NOT NULL,
	researcher_email VARCHAR(50) UNIQUE,
	officer_id INT REFERENCES ethics_compliance_officers(officer_id)
);

CREATE TABLE training_courses (
	course_id INT PRIMARY KEY,
	course_name VARCHAR(50) NOT NULL,
	course_description TEXT
);

CREATE TABLE researcher_training (
	researcher_id INT REFERENCES researchers(researcher_id),
	course_id INT REFERENCES training_courses(course_id),
	completion_date DATE,
	PRIMARY KEY (researcher_id, course_id)
);

CREATE TABLE training_progress (
	researcher_id INT REFERENCES researchers(researcher_id),
	course_id INT REFERENCES training_courses(course_id),
	progress_percentage INT CHECK (progress_percentage >= 0 AND progress_percentage <= 100),
	PRIMARY KEY (researcher_id, course_id)
);
