tables.table_schema:
CREATE TABLE oversight_committee (
    committee_id INT PRIMARY KEY,
    committee_name VARCHAR,
    committee_description TEXT
);

CREATE TABLE research_projects (
    project_id INT PRIMARY KEY,
    project_name TEXT,
    researcher_id INT,
    approval_date DATE
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name TEXT,
    researcher_email TEXT,
    institution TEXT
);

CREATE TABLE subjects (
    subject_id INT PRIMARY KEY,
    subject_name TEXT,
    subject_age INT,
    subject_gender TEXT
);

CREATE TABLE approvals (
    approval_id INT PRIMARY KEY,
    project_id INT,
    researcher_id INT,
    approval_date DATE
);
