tables.table_schema:
CREATE TABLE treatments (
    treatment_id INT PRIMARY KEY,
    treatment_name VARCHAR(50),
    duration_days INT,
    cost DECIMAL(10,2)
);

CREATE TABLE trials (
    trial_id INT PRIMARY KEY,
    trial_name VARCHAR(50),
    start_date DATE,
    end_date DATE
);

CREATE TABLE trial_patients (
    trial_id INT,
    patient_id INT,
    enrollment_date DATE,
    treatment_id INT
);

CREATE TABLE outcomes (
    outcome_id INT PRIMARY KEY,
    trial_id INT,
    patient_id INT,
    outcome VARCHAR(100)
);
