tables.table_schema:
CREATE TABLE genetic_data (
  participant_id UUID PRIMARY KEY,
  gene_sequence TEXT,
  mutation_type VARCHAR(50),
  research_area VARCHAR(50)
);

CREATE TABLE researchers (researcher_id INT PRIMARY KEY, researcher_name VARCHAR(255), research_area VARCHAR(255));
CREATE TABLE samples (sample_id INT PRIMARY KEY, sample_name VARCHAR(255), sample_type VARCHAR(50), researcher_id INT);
CREATE TABLE experiments (experiment_id INT PRIMARY KEY, experiment_name VARCHAR(255), start_date DATE, end_date DATE, researcher_id INT);
CREATE TABLE results (result_id INT PRIMARY KEY, sample_id INT, experiment_id INT, result_data TEXT);
