tables.table_schema:
CREATE TABLE telemedicine_data (
	patient_id INT PRIMARY KEY,
	symptoms TEXT,
	diagnosis TEXT,
	appointment_date DATE
);

CREATE TABLE medical_professional (
  professional_id INT PRIMARY KEY,
  name VARCHAR,
  specialty VARCHAR,
  contact_email VARCHAR
);

CREATE TABLE TelemedicineData (
    patient_id INT,
    appointment_date TIMESTAMP,
    diagnosis TEXT,
    prescription TEXT
);

CREATE TABLE EHR (
    patient_id INT,
    name VARCHAR(50),
    dob DATE,
    address VARCHAR(100)
);

CREATE TABLE PatientHistory (
    patient_id INT,
    medical_history TEXT,
    allergies TEXT,
    surgeries TEXT
);

CREATE TABLE PhysicianNotes (
    note_id INT,
    patient_id INT,
    physician_name VARCHAR(50),
    note TEXT
);
