tables.table_schema:
CREATE TABLE medication_usage (
    patient_id INT,
    medication_name VARCHAR(100),
    dosage FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE Medications (
  id INT PRIMARY KEY,
  medication_name VARCHAR(50) NOT NULL,
  dosage INT NOT NULL,
  frequency VARCHAR(20) NOT NULL
);

CREATE TABLE Patients (
  patient_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  age INT NOT NULL,
  gender VARCHAR(10) NOT NULL
);

CREATE TABLE medications (id INT PRIMARY KEY, name VARCHAR(255), dosage INT, frequency VARCHAR(50));

CREATE TABLE patients (id INT PRIMARY KEY, name VARCHAR(255), age INT, gender VARCHAR(10));

CREATE TABLE prescriptions (id INT PRIMARY KEY, medication_id INT, patient_id INT, dosage INT, frequency VARCHAR(50), FOREIGN KEY (medication_id) REFERENCES medications(id), FOREIGN KEY (patient_id) REFERENCES patients(id));

CREATE TABLE doctors (id INT PRIMARY KEY, name VARCHAR(255), specialty VARCHAR(100));

CREATE TABLE Patient (
  patient_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  date_of_birth DATE
);

CREATE TABLE Medication (
  medication_id INT PRIMARY KEY,
  medication_name VARCHAR(50),
  dosage_amount DECIMAL,
  frequency VARCHAR(50)
);

CREATE TABLE Doctor (
  doctor_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50)
);
