tables.table_schema:
CREATE TABLE ComplianceData (
    patient_id UUID PRIMARY KEY,
    compliance_score INT,
    compliance_status STRING,
    compliance_date DATE
);

CREATE TABLE PolicyGuidelines (
    guideline_id UUID PRIMARY KEY,
    guideline_title STRING,
    guideline_description STRING
);

CREATE TABLE HealthcarePolicyGuidelines (
    id UUID PRIMARY KEY,
    policy_name VARCHAR(100),
    description TEXT,
    effective_date DATE
);

CREATE TABLE ComplianceReports (
  report_id INT PRIMARY KEY,
  report_date DATE,
  compliance_rating FLOAT,
  policy_reference VARCHAR(255)
);

CREATE TABLE Inspections (
  inspection_id INT PRIMARY KEY,
  inspection_date DATE,
  department_name VARCHAR(255),
  inspector_name VARCHAR(255)
);

CREATE TABLE Policies (
  policy_id INT PRIMARY KEY,
  policy_name VARCHAR(255),
  policy_description TEXT
);

CREATE TABLE Departments (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(255),
  responsible_person VARCHAR(255)
);
