tables.table_schema:
CREATE TABLE environmental_factors ( 
    factor_id INT PRIMARY KEY, 
    factor_name VARCHAR(50) NOT NULL, 
    country VARCHAR(50) NOT NULL, 
    factor_value FLOAT NOT NULL 
);

CREATE TABLE public_health_issues ( 
    issue_id INT PRIMARY KEY, 
    issue_name VARCHAR(50) NOT NULL, 
    country VARCHAR(50) NOT NULL, 
    cases_reported INT NOT NULL 
);

CREATE TABLE environmental_factors (
    factor_id INT PRIMARY KEY,
    factor_name VARCHAR(50) NOT NULL,
    factor_description TEXT
);

CREATE TABLE public_health_issues (
    issue_id INT PRIMARY KEY,
    issue_name VARCHAR(50) NOT NULL,
    issue_description TEXT
);

CREATE TABLE countries (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50) NOT NULL
);

CREATE TABLE EnvironmentalFactors (
  country_code VARCHAR(3),
  temperature FLOAT,
  humidity FLOAT,
  pollution_level FLOAT
);

CREATE TABLE HealthIssues (
  country_code VARCHAR(3),
  disease VARCHAR(50),
  death_rate FLOAT,
  recovery_rate FLOAT
);

CREATE TABLE PopulationStatistics (
  country_code VARCHAR(3),
  population INT,
  average_age FLOAT,
  birth_rate FLOAT
);

CREATE TABLE ResearchData (
  research_id INT,
  country_code VARCHAR(3),
  topic VARCHAR(100),
  publication_date DATE
);

CREATE TABLE DataMiningResults (
  result_id INT,
  research_id INT,
  correlation_score FLOAT,
  significance_level FLOAT
);
