tables.table_schema:
CREATE TABLE healthcare_professionals (
  id INT PRIMARY KEY,
  name VARCHAR(100),
  specialization VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE HealthcareProfessionals (
  professional_id UUID PRIMARY KEY,
  professional_name VARCHAR(50) NOT NULL,
  specialization VARCHAR(50),
  hospital_id UUID REFERENCES Hospitals(hospital_id)
);

CREATE TABLE HealthcareProfessionals (
  professional_id INT PRIMARY KEY,
  professional_name VARCHAR(255) NOT NULL,
  specialization VARCHAR(50),
  department VARCHAR(50)
);

CREATE TABLE healthcare_professionals (
    professional_id INT PRIMARY KEY,
    professional_name VARCHAR(100),
    specialization VARCHAR(50)
);

CREATE TABLE test_types (
    type_id INT PRIMARY KEY,
    test_name VARCHAR(50),
    normal_range_min INT,
    normal_range_max INT
);
