tables.table_schema:
CREATE TABLE TherapyTechniques (
    technique_id INT PRIMARY KEY,
    technique_name VARCHAR(255) NOT NULL
);

CREATE TABLE Interventions (
    intervention_id INT PRIMARY KEY,
    intervention_name VARCHAR(255) NOT NULL,
    intervention_category VARCHAR(50)
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255) NOT NULL,
    therapy_type VARCHAR(100),
    start_date DATE
);

CREATE TABLE therapy_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(100) UNIQUE
);

CREATE TABLE therapists (
    therapist_id INT PRIMARY KEY,
    therapist_name VARCHAR(255) NOT NULL
);

CREATE TABLE Therapists (
  therapist_id UUID PRIMARY KEY,
  therapist_name VARCHAR(50) NOT NULL,
  specialization VARCHAR(50),
  contact_number VARCHAR(20)
);

CREATE TABLE Therapies (
  therapy_id UUID PRIMARY KEY,
  therapy_name VARCHAR(50) NOT NULL,
  duration INT,
  cost DECIMAL(10, 2)
);
