tables.table_schema:
CREATE TABLE healthcare_quality_indicators (
    patient_id INT PRIMARY KEY,
    indicator_name VARCHAR(255) NOT NULL,
    indicator_value DECIMAL(10,2) NOT NULL,
    industry_standard DECIMAL(10,2) NOT NULL
);

CREATE TABLE healthcare_providers (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR(255) NOT NULL,
  provider_type VARCHAR(100) NOT NULL,
  location VARCHAR(255) NOT NULL
);

CREATE TABLE healthcare_patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(255) NOT NULL,
  patient_age INT NOT NULL,
  patient_gender VARCHAR(10) NOT NULL
);

CREATE TABLE healthcare_metrics (
  metric_id INT PRIMARY KEY,
  metric_name VARCHAR(255) NOT NULL,
  benchmark_value DECIMAL(10,2) NOT NULL,
  performance_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE healthcare_ratings (
  rating_id INT PRIMARY KEY,
  provider_id INT NOT NULL,
  patient_id INT NOT NULL,
  rating_value DECIMAL(5,2) NOT NULL
);

CREATE TABLE Hospital (
	id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	location VARCHAR(255) NOT NULL
);

CREATE TABLE Treatment (
	id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	patient_id INT,
	FOREIGN KEY (patient_id) REFERENCES Patient(id)
);

CREATE TABLE Indicator (
	id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	threshold INT
);
