tables.table_schema:
CREATE TABLE adverse_events (
    event_id INT PRIMARY KEY,
    event_description TEXT,
    event_date DATE
);

CREATE TABLE regulatory_agencies (
    agency_id INT PRIMARY KEY,
    agency_name TEXT,
    agency_type TEXT
);

CREATE TABLE AdverseEvents (
    event_id INT PRIMARY KEY,
    event_type VARCHAR(50),
    event_date DATE,
    event_description TEXT
);

CREATE TABLE RegulatoryAgencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(100),
    agency_type VARCHAR(50)
);

CREATE TABLE HealthcareQuality (
    quality_id INT PRIMARY KEY,
    quality_metric VARCHAR(100),
    patient_safety_rating INT
);

CREATE TABLE agencies (
  agency_id INT PRIMARY KEY,
  agency_name VARCHAR(100) NOT NULL,
  region VARCHAR(50) NOT NULL
);
