tables.table_schema:
CREATE TABLE resources (id INT PRIMARY KEY, title VARCHAR(255), author VARCHAR(255), content TEXT, category VARCHAR(50));

CREATE TABLE articles (id INT PRIMARY KEY, title VARCHAR(255), author VARCHAR(255), content TEXT, publish_date DATE);

CREATE TABLE staff (id INT PRIMARY KEY, name VARCHAR(100), role VARCHAR(50), email VARCHAR(100), phone VARCHAR(15));

CREATE TABLE Staff (
    staff_id INT PRIMARY KEY,
    staff_name VARCHAR(100) NOT NULL,
    role VARCHAR(50) NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE AccessLogs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(50) NOT NULL
);
