tables.table_schema:
CREATE TABLE researcher_healthcare_data (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_topic VARCHAR(100),
    healthcare_data TEXT
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE HealthcareData (
    data_id INT PRIMARY KEY,
    patient_id INT NOT NULL,
    disease_type VARCHAR(50) NOT NULL,
    outcome VARCHAR(50) NOT NULL
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    researcher_email VARCHAR(100) NOT NULL
);

CREATE TABLE HealthcareData (
    data_id INT PRIMARY KEY,
    patient_id INT NOT NULL,
    disease_id INT NOT NULL,
    data_value FLOAT NOT NULL
);

CREATE TABLE DiseaseOutcomes (
    outcome_id INT PRIMARY KEY,
    disease_id INT NOT NULL,
    outcome_description TEXT
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  research_area VARCHAR(100)
);

CREATE TABLE studies (
  study_id INT PRIMARY KEY,
  study_name VARCHAR(100),
  researcher_id INT,
  study_date DATE
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE diagnoses (
  diagnosis_id INT PRIMARY KEY,
  patient_id INT,
  disease_name VARCHAR(50),
  diagnosis_date DATE
);

CREATE TABLE outcomes (
  outcome_id INT PRIMARY KEY,
  study_id INT,
  diagnosis_id INT,
  outcome_result VARCHAR(50)
);
