tables.table_schema:
CREATE TABLE outbreaks (
  id INT PRIMARY KEY,
  disease_name VARCHAR(255),
  location VARCHAR(255),
  date DATE
);

CREATE TABLE population_health_data (
    data_id INT PRIMARY KEY,
    city VARCHAR(255),
    population_size INT,
    health_indicators JSON
);

CREATE TABLE health_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    contact_person VARCHAR(255),
    contact_email VARCHAR(255)
);

CREATE TABLE diseases (
    disease_id SERIAL PRIMARY KEY,
    disease_name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE outbreaks (
    outbreak_id SERIAL PRIMARY KEY,
    disease_id INT NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    city VARCHAR(50) NOT NULL,
    country VARCHAR(50) NOT NULL
);

CREATE TABLE patient_outbreak (
    patient_id INT NOT NULL,
    outbreak_id INT NOT NULL,
    FOREIGN KEY (patient_id) REFERENCES patients(patient_id),
    FOREIGN KEY (outbreak_id) REFERENCES outbreaks(outbreak_id)
);
