tables.table_schema:
CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(100) NOT NULL,
    research_area VARCHAR(100) NOT NULL
);

CREATE TABLE MentalHealthPatients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(100) NOT NULL,
    patient_age INT,
    diagnosis TEXT
);

CREATE TABLE patients (patient_id INT, patient_name VARCHAR(255), age INT, gender VARCHAR(10), diagnosis VARCHAR(255));
CREATE TABLE treatments (treatment_id INT, treatment_name VARCHAR(255), provider_id INT, cost DECIMAL(10, 2), start_date DATE, end_date DATE);
CREATE TABLE providers (provider_id INT, provider_name VARCHAR(255), specialty VARCHAR(100), location VARCHAR(255));
CREATE TABLE outcomes (outcome_id INT, patient_id INT, treatment_id INT, outcome VARCHAR(255), satisfaction_rating INT);

CREATE TABLE PatientDemographics (
  patient_id INT PRIMARY KEY,
  age INT,
  gender VARCHAR(10),
  diagnosis VARCHAR(50)
);

CREATE TABLE PatientOutcomes (
  patient_id INT PRIMARY KEY,
  treatment VARCHAR(50),
  outcome VARCHAR(50),
  recovery_time INT
);

CREATE TABLE ResearcherInfo (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  specialization VARCHAR(50)
);

CREATE TABLE ResearcherReports (
  report_id INT PRIMARY KEY,
  researcher_id INT,
  report_title VARCHAR(100),
  analysis_results TEXT
);

CREATE TABLE DataMiningLogs (
  log_id INT PRIMARY KEY,
  researcher_id INT,
  log_datetime TIMESTAMP,
  activity_description TEXT
);
