tables.table_schema:
CREATE TABLE healthcare_disparities (
  organization_id INT,
  population_group VARCHAR,
  healthcare_access_score FLOAT,
  healthcare_utilization_score FLOAT
);

CREATE TABLE hospital (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255),
    capacity INT
);

CREATE TABLE healthcare_provider (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    specialty VARCHAR(50),
    location VARCHAR(255)
);

CREATE TABLE insurance (
    id INT PRIMARY KEY,
    provider VARCHAR(255),
    coverage VARCHAR(50),
    cost INT
);
