tables.table_schema:
CREATE TABLE patient_satisfaction_survey (
  patient_id INT,
  satisfaction_score INT,
  comments TEXT
);

CREATE TABLE patient_surveys (
	survey_id UUID PRIMARY KEY,
	patient_id UUID,
	satisfaction_score INT,
	survey_date DATE
);

CREATE TABLE healthcare_services (
	service_id UUID PRIMARY KEY,
	service_name VARCHAR(50),
	service_type VARCHAR(50),
	service_rating INT
);

CREATE TABLE healthcare_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    service_description TEXT,
    service_rating DECIMAL
);

CREATE TABLE Question { 
 question_id INT PRIMARY KEY, 
 question_text STRING 
}

CREATE TABLE SurveyQuestionMap { 
 response_id UUID REFERENCES SurveyResponse(response_id), 
 question_id INT REFERENCES Question(question_id) 
}
