tables.table_schema:
CREATE TABLE communities (
    community_id INT PRIMARY KEY,
    community_name VARCHAR(100),
    region VARCHAR(50),
    population INT
);

CREATE TABLE resource_trends (
    trend_id INT PRIMARY KEY,
    resource_type VARCHAR(50),
    trend_date DATE,
    average_quantity FLOAT
);

CREATE TABLE Healthcare_Providers (
    provider_id UUID PRIMARY KEY,
    provider_name VARCHAR(100),
    location VARCHAR(100),
    expertise VARCHAR(100)
);

CREATE TABLE Patients (
    patient_id UUID PRIMARY KEY,
    patient_name VARCHAR(100),
    age INT,
    condition VARCHAR(100)
);

CREATE TABLE Healthcare_Resources (
    resource_id UUID PRIMARY KEY,
    resource_name VARCHAR(100),
    type VARCHAR(100),
    location VARCHAR(100)
);

CREATE TABLE Community_Health_Status (
    community_id UUID PRIMARY KEY,
    community_name VARCHAR(100),
    population INT,
    health_status VARCHAR(100)
);

CREATE TABLE Hospital (
	hospital_id INT PRIMARY KEY,
	hospital_name VARCHAR(255),
	hospital_location VARCHAR(255),
	hospital_rating FLOAT
);

CREATE TABLE HealthcareResource (
	resource_id INT PRIMARY KEY,
	resource_name VARCHAR(50),
	resource_type VARCHAR(50),
	quantity INT
);
