tables.table_schema:
CREATE TABLE PatientData (
    patient_id INT,
    timestamp TIMESTAMP,
    heart_rate INT,
    body_temp FLOAT
);

CREATE TABLE DeviceData (
    device_id INT,
    timestamp TIMESTAMP,
    heart_rate INT,
    steps_count INT
);

CREATE TABLE PatientDevice (
    patient_id INT,
    device_id INT
);

CREATE TABLE wearable_devices (
    device_id INTEGER PRIMARY KEY,
    patient_id INTEGER,
    device_type VARCHAR(50),
    date_started DATE
);

CREATE TABLE patients (
    patient_id INTEGER PRIMARY KEY,
    patient_name VARCHAR(50),
    birthdate DATE,
    gender VARCHAR(10)
);

CREATE TABLE researchers (
    researcher_id INTEGER PRIMARY KEY,
    researcher_name VARCHAR(50),
    department VARCHAR(50)
);
